import React, { useState } from 'react';
import './Evaluations.css';
import { ReactComponent as SearchIcon } from '../assets/search-icon.svg';
import { ReactComponent as EyeIcon } from '../assets/eye-icon.svg';

function Evaluations() {
  const [searchTerm, setSearchTerm] = useState('');
  
  // Sample data for evaluations
  const evaluations = [
    { id: '0123M', product: 'Leche Z', evaluator: 'Juan P', type: 'Microbio', date: '20/03/24' },
    { id: '0124S', product: 'Leche Z', evaluator: 'Sara Z', type: 'Sensorial', date: '18/03/24' },
    { id: '0864M', product: 'Queso A', evaluator: 'John D', type: 'Microbio', date: '17/03/24' },
    { id: '0865S', product: 'Queso A', evaluator: 'Guy U', type: 'Sensorial', date: '15/03/24' },
    { id: '0567M', product: 'Peras B', evaluator: 'Didi A', type: 'Microbio', date: '8/03/24' },
    { id: '0568S', product: 'Peras B', evaluator: 'Davo Z', type: 'Sensorial', date: '7/03/24' },
    { id: '0345M', product: 'Queso Z', evaluator: 'Alex G', type: 'Microbio', date: '5/03/24' },
    { id: '0346M', product: 'Queso Z', evaluator: 'Sara Z', type: 'Sensorial', date: '4/03/24' },
  ];

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleViewEvaluation = (id) => {
    console.log(`Viewing evaluation ${id}`);
    // Here you would navigate to the evaluation details page
  };

  return (
    <div className="evaluations-container">
      <div className="search-filter-bar">
        <div className="search-bar">
          <SearchIcon className="search-icon" />
          <input
            type="text"
            placeholder="Buscar evaluaciones"
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <button className="filter-button">Agregar filtro</button>
      </div>

      <div className="evaluations-table-container">
        <h2>Evaluaciones</h2>
        <div className="table-wrapper">
          <table className="evaluations-table">
            <thead>
              <tr>
                <th>ID Eval.</th>
                <th>Producto</th>
                <th>Evaluador</th>
                <th>Tipo</th>
                <th>Fecha</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {evaluations.map((evaluation) => (
                <tr key={evaluation.id}>
                  <td>{evaluation.id}</td>
                  <td>{evaluation.product}</td>
                  <td>{evaluation.evaluator}</td>
                  <td>{evaluation.type}</td>
                  <td>{evaluation.date}</td>
                  <td>
                    <button 
                      className="view-button"
                      onClick={() => handleViewEvaluation(evaluation.id)}
                    >
                      <EyeIcon className="eye-icon" />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Evaluations;
