import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './UserProfile.css';
import { ReactComponent as LogoutIcon } from '../assets/logout-icon.svg';

const roles = {
  1: 'Administrador',
  2: 'Evaluador',
  3: 'Supervisor'
};

function UserProfile() {
  const navigate = useNavigate();
  const { currentUser, logout } = useAuth();

  const handleEdit = () => {
    navigate('/profile/edit');
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  if (!currentUser) {
    navigate('/login');
    return null;
  }

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <div className="profile-avatar">
            <img src={process.env.PUBLIC_URL + '/default-avatar.png'} alt="User avatar" />
          </div>
          <button className="edit-button" onClick={handleEdit}>
            Editar
          </button>
        </div>

        <div className="profile-details">
          <div className="profile-item">
            <label>Nombre:</label>
            <span>{`${currentUser.nombre} ${currentUser.apellido}`}</span>
          </div>

          <div className="profile-item">
            <label>Correo:</label>
            <span>{currentUser.correo}</span>
          </div>

          <div className="profile-item">
            <label>Contraseña:</label>
            <span>••••••••</span>
          </div>

          <div className="profile-item">
            <label>Teléfono:</label>
            <span>{currentUser.telefono}</span>
          </div>

          <div className="profile-item">
            <label>Rol:</label>
            <span>{roles[currentUser.id_rol] || 'Desconocido'}</span>
          </div>

          <div className="profile-item">
            <label>Fecha de registro:</label>
            <span>{currentUser.fecha_registro}</span>
          </div>
        </div>

        <div className="logout-section">
          <button className="logout-button" onClick={handleLogout}>
            <LogoutIcon className="logout-icon" />
            <span>Cerrar sesión</span>
          </button>
        </div>
      </div>
    </div>
  );
}

export default UserProfile;
