// src/components/EvaluationDetail.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import './EvaluationDetail.css';
import { ReactComponent as BackIcon } from '../assets/back-icon.svg';
import { useParams } from 'react-router-dom';


function EvaluationDetail() {
  const location = useLocation();
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  // Si vienes del Dashboard con mode=create
  const isCreateMode = location.state?.mode === 'create';
  // Si vienes a ver/editar una evaluación existente
  const { id: urlId } = useParams();
  const evaluationId = location.state?.evaluationId || urlId || null;
  // Solo editable en modo create
  const readOnly = !isCreateMode;

  const [formData, setFormData] = useState({
    // En create: id pendiente, en detail: lo rellena el useEffect
    id: evaluationId || 'Pendiente',
    id_producto: '',
    id_usuario: '',                
    id_tipo_evaluacion: '',
    fecha_evaluacion: new Date().toISOString().split('T')[0],
    observaciones: ''
  });

  const [productos, setProductos] = useState([]);
  const [tiposEval, setTiposEval] = useState([]);

  // 1) Cargo dropdown de productos
  useEffect(() => {
    axios.get('http://localhost:4000/api/productos', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setProductos(res.data.body))
    .catch(err => console.error(err));
  }, [token]);

  // 2) Cargo dropdown de tipos de evaluación
  useEffect(() => {
    axios.get('http://localhost:4000/api/tipo_criterio', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setTiposEval(res.data.body))
    .catch(err => console.error(err));
  }, [token]);

 // 3) Si estoy editando (detail), traigo datos existentes
useEffect(() => {
  if (evaluationId && !isCreateMode && productos.length > 0 && tiposEval.length > 0) {
    axios.get(`http://localhost:4000/api/evaluaciones/${evaluationId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => {
      const d = res.data.body;
      setFormData({
        id: d.id,
        id_producto: d.id_producto.toString(),
        id_usuario: d.id_usuario.toString(),
        id_tipo_evaluacion: d.id_tipo_evaluacion.toString(),
        fecha_evaluacion: d.fecha_evaluacion.substring(0, 10),
        observaciones: d.observaciones || ''
      });
    })
    .catch(err => console.error(err));
  }
}, [evaluationId, isCreateMode, token, productos, tiposEval]);

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:4000/api/evaluaciones', formData, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(() => {
      // tras crear vuelvo al dashboard, tras editar a la lista
      navigate(isCreateMode ? '/dashboard' : '/evaluaciones');
    })
    .catch(err => console.error(err));
  };

  const handleGoBack = () => {
    // si vine de dashboard vuelvo allí, si no a la lista
    navigate(isCreateMode ? '/dashboard' : '/evaluaciones');
  };

  return (
    <div className="evaluation-detail-container">
      <div className="evaluation-detail-card">
        <div className="detail-header">
          <button className="back-button" onClick={handleGoBack}>
            <BackIcon className="back-icon" />
            <span>Regresar</span>
          </button>
          <h2>{ isCreateMode ? 'Nueva Evaluación' : 'Detalle de Evaluación' }</h2>
        </div>

        <form onSubmit={handleSubmit}>
          {/* ID Evaluación */}
          <div className="detail-item">
            <label>ID Evaluación:</label>
            <div className="detail-value">{formData.id}</div>
          </div>

          {/* Producto */}
          <div className="detail-item">
            <label>Producto:</label>
            {readOnly ? (
              <div className="detail-value">
                { productos.find(p => p.id.toString() === formData.id_producto.toString())?.nombre || '—' }
              </div>
            ) : (
              <select
                name="id_producto"
                value={formData.id_producto}
                onChange={handleChange}
                required
              >
                <option value="">Seleccione un producto</option>
                {productos.map(p => (
                  <option key={p.id} value={p.id}>{p.nombre}</option>
                ))}
              </select>
            )}
          </div>

          {/* Tipo de evaluación */}
          <div className="detail-item">
            <label>Tipo de Evaluación:</label>
            {readOnly ? (
              <div className="detail-value">
                { tiposEval.find(t => t.id === formData.id_tipo_evaluacion)?.nombre_tipo_evaluacion || '—' }
              </div>
            ) : (
              <select
                name="id_tipo_evaluacion"
                value={formData.id_tipo_evaluacion}
                onChange={handleChange}
                required
              >
                <option value="">Seleccione un tipo</option>
                {tiposEval.map(t => (
                  <option key={t.id} value={t.id}>{t.nombre_tipo_evaluacion}</option>
                ))}
              </select>
            )}
          </div>

          {/* Fecha */}
          <div className="detail-item">
            <label>Fecha:</label>
            <div className="detail-value">{formData.fecha_evaluacion}</div>
          </div>

          {/* Observaciones */}
          <div className="detail-item">
            <label>Observaciones:</label>
            {readOnly ? (
              <div className="detail-value">{formData.observaciones || '—'}</div>
            ) : (
              <textarea
                name="observaciones"
                value={formData.observaciones}
                onChange={handleChange}
                placeholder="Agregar observaciones..."
              />
            )}
          </div>

          {/* Botones */}
          {!readOnly && (
            <div className="detail-actions">
              <button type="submit" className="save-button">
                { isCreateMode ? 'Crear Evaluación' : 'Guardar' }
              </button>
            </div>
          )}
        </form>
      </div>
    </div>
  );
}

export default EvaluationDetail;
