// src/components/EvaluationDetail.js
import React, { useEffect, useState } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import './EvaluationDetail.css';
import { ReactComponent as BackIcon } from '../assets/back-icon.svg';

function EvaluationDetail() {
  const { id: urlId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  const isCreateMode = location.state?.mode === 'create';
  const from = location.state?.from || 'list';
  const evaluationId = urlId || null;

  const [loading, setLoading] = useState(!isCreateMode);
  const [formData, setFormData] = useState({
    id: '',
    id_producto: '',
    id_usuario: '',
    id_tipo_evaluacion: '',
    fecha_evaluacion: new Date().toISOString().slice(0, 10),
    observaciones: ''
  });

  const [productos, setProductos] = useState([]);
  const [usuarios, setUsuarios]   = useState([]);
  const [tiposEval, setTiposEval] = useState([]);

  // 1) Traer catálogo de productos
  useEffect(() => {
    fetch('http://localhost:4000/api/productos', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(r => r.json())
      .then(d => setProductos(d.body))
      .catch(console.error);
  }, [token]);

  // 2) Traer catálogo de usuarios
  useEffect(() => {
    fetch('http://localhost:4000/api/usuarios', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(r => r.json())
      .then(d => setUsuarios(d.body))
      .catch(console.error);
  }, [token]);

  // 3) Traer catálogo de tipos de evaluación
  useEffect(() => {
    fetch('http://localhost:4000/api/tipo_criterio', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(r => r.json())
      .then(d => setTiposEval(d.body))
      .catch(console.error);
  }, [token]);

  // 4) Traer datos de la evaluación (solo en modo lectura)
  useEffect(() => {
    if (!isCreateMode && evaluationId) {
      fetch(`http://localhost:4000/api/evaluaciones/${evaluationId}`, {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(r => r.json())
        .then(d => {
          const e = d.body;
          setFormData({
            id: e.id.toString(),
            id_producto: e.id_producto.toString(),
            id_usuario: e.id_usuario.toString(),
            id_tipo_evaluacion: e.id_tipo_evaluacion.toString(),
            fecha_evaluacion: e.fecha_evaluacion.slice(0,10),
            observaciones: e.observaciones || ''
          });
        })
        .catch(console.error)
        .finally(() => setLoading(false));
    } else {
      // Si es create mode, no necesitamos cargar nada
      setLoading(false);
    }
  }, [isCreateMode, evaluationId, token]);

  const handleGoBack = () => {
    navigate(from === 'dashboard' ? '/dashboard' : '/evaluaciones');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // En create mode insertamos; en readOnly no aparece botón
    fetch('http://localhost:4000/api/evaluaciones', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify({
        ...formData,
        id: isCreateMode ? 0 : formData.id
      })
    })
      .then(r => {
        if (!r.ok) throw new Error('Error al guardar');
        return r.json();
      })
      .then(() => navigate('/dashboard'))
      .catch(console.error);
  };

  if (loading) {
    return <div className="evaluation-detail-container">Cargando...</div>;
  }

  // Helpers para mostrar nombres en modo lectura
  const nombreProducto = productos.find(p => p.id.toString() === formData.id_producto)?.nombre || '—';
  const nombreUsuario  = usuarios.find(u => u.id.toString() === formData.id_usuario)?.nombre + ' ' +
                         usuarios.find(u => u.id.toString() === formData.id_usuario)?.apellido || '—';
  const nombreTipo     = tiposEval.find(t => t.id.toString() === formData.id_tipo_evaluacion)
                         ?.nombre_tipo_evaluacion || '—';

  return (
    <div className="evaluation-detail-container">
      <div className="evaluation-detail-card">
        <div className="detail-header">
          <button className="back-button" onClick={handleGoBack}>
            <BackIcon className="back-icon" />
            <span>Regresar</span>
          </button>
          <h2>{isCreateMode ? 'Nueva Evaluación' : 'Detalle de Evaluación'}</h2>
        </div>

        <form onSubmit={handleSubmit}>
          {/* ID Evaluación */}
          <div className="detail-item">
            <label>ID Evaluación:</label>
            <div className="detail-value">
              {isCreateMode ? 'Pendiente' : formData.id}
            </div>
          </div>

          {/* Producto */}
          <div className="detail-item">
            <label>Producto:</label>
            {isCreateMode ? (
              <select
                name="id_producto"
                value={formData.id_producto}
                onChange={e => setFormData({...formData, id_producto: e.target.value})}
                required
              >
                <option value="">Selecciona un producto</option>
                {productos.map(p => (
                  <option key={p.id} value={p.id}>{p.nombre}</option>
                ))}
              </select>
            ) : (
              <div className="detail-value">{nombreProducto}</div>
            )}
          </div>

          {/* Usuario */}
          <div className="detail-item">
            <label>Evaluador:</label>
            {isCreateMode ? (
              <select
                name="id_usuario"
                value={formData.id_usuario}
                onChange={e => setFormData({...formData, id_usuario: e.target.value})}
                required
              >
                <option value="">Selecciona un usuario</option>
                {usuarios.map(u => (
                  <option key={u.id} value={u.id}>{u.nombre} {u.apellido}</option>
                ))}
              </select>
            ) : (
              <div className="detail-value">{nombreUsuario}</div>
            )}
          </div>

          {/* Tipo de Evaluación */}
          <div className="detail-item">
            <label>Tipo de Evaluación:</label>
            {isCreateMode ? (
              <select
                name="id_tipo_evaluacion"
                value={formData.id_tipo_evaluacion}
                onChange={e => setFormData({...formData, id_tipo_evaluacion: e.target.value})}
                required
              >
                <option value="">Selecciona un tipo</option>
                {tiposEval.map(t => (
                  <option key={t.id} value={t.id}>{t.nombre_tipo_evaluacion}</option>
                ))}
              </select>
            ) : (
              <div className="detail-value">{nombreTipo}</div>
            )}
          </div>

          {/* Fecha */}
          <div className="detail-item">
            <label>Fecha:</label>
            <div className="detail-value">{formData.fecha_evaluacion}</div>
          </div>

          {/* Observaciones */}
          <div className="detail-item">
            <label>Observaciones:</label>
            {isCreateMode ? (
              <textarea
                name="observaciones"
                value={formData.observaciones}
                onChange={e => setFormData({...formData, observaciones: e.target.value})}
                placeholder="Escribe aquí..."
              />
            ) : (
              <div className="detail-value">{formData.observaciones || '—'}</div>
            )}
          </div>

          {/* Botón Crear (solo en create) */}
          {isCreateMode && (
            <div className="detail-actions">
              <button type="submit" className="save-button">
                Crear Evaluación
              </button>
            </div>
          )}
        </form>
      </div>
    </div>
  );
}

export default EvaluationDetail;
