import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './EditProfile.css';
import { ReactComponent as LogoutIcon } from '../assets/logout-icon.svg';

function EditProfile() {
  const navigate = useNavigate();
  const { currentUser, logout } = useAuth();
  
  const [userData, setUserData] = useState(
    currentUser || {
      name: '',
      email: '',
      password: '',
      phone: '',
      role: '',
      registrationDate: ''
    }
  );

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({
      ...userData,
      [name]: value
    });
  };

  const handleCancel = () => {
    navigate('/profile');
  };

  const handleLogout = () => {
    logout();
    // Redirect to landing page
    navigate('/');
  };

  const handleSave = (e) => {
    e.preventDefault();
    // Here you would save the user data to the backend
    console.log('Saving user data:', userData);
    navigate('/profile');
  };

  // If no user is found, redirect to login
  if (!currentUser) {
    navigate('/login');
    return null;
  }

  return (
    <div className="edit-profile-container">
      <div className="edit-profile-card">
        <div className="profile-header">
          <div className="profile-avatar">
            <img src={process.env.PUBLIC_URL + '/default-avatar.png'} alt="User avatar" />
            <div className="upload-overlay">
              <span>Cambiar foto</span>
            </div>
          </div>
          
          <button className="save-button" form="profile-form">
            Guardar
          </button>
        </div>
        
        <form id="profile-form" className="edit-form" onSubmit={handleSave}>
          <div className="form-group">
            <label htmlFor="name">Nombre:</label>
            <input 
              type="text" 
              id="name"
              name="name" 
              value={userData.name} 
              onChange={handleChange} 
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="email">Correo:</label>
            <input 
              type="email" 
              id="email"
              name="email" 
              value={userData.email} 
              onChange={handleChange} 
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Contraseña:</label>
            <input 
              type="password" 
              id="password"
              name="password" 
              value={userData.password} 
              onChange={handleChange} 
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="phone">Teléfono:</label>
            <input 
              type="tel" 
              id="phone"
              name="phone" 
              value={userData.phone} 
              onChange={handleChange} 
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="role">Rol:</label>
            <input 
              type="text" 
              id="role"
              name="role" 
              value={userData.role} 
              readOnly
              className="readonly-field"
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="registrationDate">Fecha de registro:</label>
            <input 
              type="text" 
              id="registrationDate"
              name="registrationDate" 
              value={userData.registrationDate} 
              readOnly
              className="readonly-field"
            />
          </div>
        </form>
        
        <div className="action-buttons">
          <button className="cancel-button" onClick={handleCancel}>
            Cancelar
          </button>
          
          <button className="logout-button" onClick={handleLogout}>
            <LogoutIcon className="logout-icon" />
            <span>Cerrar sesión</span>
          </button>
        </div>
      </div>
    </div>
  );
}

export default EditProfile;
