// src/components/EvaluationDetail.js
import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import './EvaluationDetail.css';
import { ReactComponent as BackIcon } from '../assets/back-icon.svg';

function EvaluationDetail() {
  const location = useLocation();
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  const isCreateMode = location.state?.mode === 'create';
  const { id: urlId } = useParams();
  const evaluationId = location.state?.evaluationId || urlId || null;
  const readOnly = !isCreateMode;

  const [formData, setFormData] = useState({
    id: evaluationId || 'Pendiente',
    id_producto: '',
    id_usuario: '',                
    id_tipo_evaluacion: '',
    fecha_evaluacion: new Date().toISOString().split('T')[0],
    observaciones: ''
  });

  const [productos, setProductos] = useState([]);
  const [tiposEval, setTiposEval] = useState([]);
  const [usuarios, setUsuarios] = useState([]);
  const [catalogsLoaded, setCatalogsLoaded] = useState(false);

  // 1) Productos
  useEffect(() => {
    fetch('http://localhost:4000/api/productos', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setProductos(data.body))
      .catch(err => console.error(err));
  }, [token]);

  // 2) Tipos de Evaluación
  useEffect(() => {
    fetch('http://localhost:4000/api/tipo_criterio', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setTiposEval(data.body))
      .catch(err => console.error(err));
  }, [token]);

  // 3) Usuarios (para mostrar el nombre del evaluador)
  useEffect(() => {
    fetch('http://localhost:4000/api/usuarios', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setUsuarios(data.body))
      .catch(err => console.error(err));
  }, [token]);

  // 4) Bandera de carga completa de catálogos
  useEffect(() => {
    if (productos.length > 0 && tiposEval.length > 0 && usuarios.length > 0) {
      setCatalogsLoaded(true);
    }
  }, [productos, tiposEval, usuarios]);

  // 5) Detalle de evaluación
  useEffect(() => {
    if (evaluationId && !isCreateMode && catalogsLoaded) {
      fetch(`http://localhost:4000/api/evaluaciones/${evaluationId}`, {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(res => res.json())
        .then(data => {
          const d = data.body;
          setFormData({
            id: d.id,
            id_producto: d.id_producto?.toString() || '',
            id_usuario: d.id_usuario?.toString() || '',
            id_tipo_evaluacion: d.id_tipo_evaluacion?.toString() || '',
            fecha_evaluacion: d.fecha_evaluacion?.substring(0, 10) || '',
            observaciones: d.observaciones || ''
          });
        })
        .catch(err => console.error(err));
    }
  }, [evaluationId, isCreateMode, token, catalogsLoaded]);

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch('http://localhost:4000/api/evaluaciones', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify(formData)
    })
      .then(res => {
        if (!res.ok) throw new Error('Error al crear evaluación');
        return res.json();
      })
      .then(() => {
        navigate(isCreateMode ? '/dashboard' : '/evaluaciones');
      })
      .catch(err => console.error(err));
  };

  const handleGoBack = () => {
    navigate(isCreateMode ? '/dashboard' : '/evaluaciones');
  };

  if (!catalogsLoaded && !isCreateMode) {
    return <div className="evaluation-detail-container">Cargando...</div>;
  }

  return (
    <div className="evaluation-detail-container">
      <div className="evaluation-detail-card">
        <div className="detail-header">
          <button className="back-button" onClick={handleGoBack}>
            <BackIcon className="back-icon" />
            <span>Regresar</span>
          </button>
          <h2>{isCreateMode ? 'Nueva Evaluación' : 'Detalle de Evaluación'}</h2>
        </div>

        <form onSubmit={handleSubmit}>
          {/* ID Evaluación */}
          <div className="detail-item">
            <label>ID Evaluación:</label>
            <div className="detail-value">{formData.id}</div>
          </div>

          {/* Producto */}
          <div className="detail-item">
            <label>Producto:</label>
            {readOnly ? (
              <div className="detail-value">
                {productos.find(p => p.id.toString() === formData.id_producto)?.nombre || '—'}
              </div>
            ) : (
              <select
                name="id_producto"
                value={formData.id_producto}
                onChange={handleChange}
                required
              >
                <option value="">Seleccione un producto</option>
                {productos.map(p => (
                  <option key={p.id} value={p.id}>{p.nombre}</option>
                ))}
              </select>
            )}
          </div>

          {/* Tipo de evaluación */}
          <div className="detail-item">
            <label>Tipo de Evaluación:</label>
            {readOnly ? (
              <div className="detail-value">
                {tiposEval.find(t => t.id.toString() === formData.id_tipo_evaluacion)?.nombre_tipo_evaluacion || '—'}
              </div>
            ) : (
              <select
                name="id_tipo_evaluacion"
                value={formData.id_tipo_evaluacion}
                onChange={handleChange}
                required
              >
                <option value="">Seleccione un tipo</option>
                {tiposEval.map(t => (
                  <option key={t.id} value={t.id}>{t.nombre_tipo_evaluacion}</option>
                ))}
              </select>
            )}
          </div>

          {/* Usuario */}
          {!isCreateMode && (
            <div className="detail-item">
              <label>Usuario Evaluador:</label>
              <div className="detail-value">
                {usuarios.find(u => u.id.toString() === formData.id_usuario)?.nombre || '—'}
              </div>
            </div>
          )}

          {/* Fecha */}
          <div className="detail-item">
            <label>Fecha:</label>
            <div className="detail-value">{formData.fecha_evaluacion}</div>
          </div>

          {/* Observaciones */}
          <div className="detail-item">
            <label>Observaciones:</label>
            {readOnly ? (
              <div className="detail-value">{formData.observaciones || '—'}</div>
            ) : (
              <textarea
                name="observaciones"
                value={formData.observaciones}
                onChange={handleChange}
                placeholder="Agregar observaciones..."
              />
            )}
          </div>

          {/* Botón de guardar */}
          {!readOnly && (
            <div className="detail-actions">
              <button type="submit" className="save-button">
                {isCreateMode ? 'Crear Evaluación' : 'Guardar'}
              </button>
            </div>
          )}
        </form>
      </div>
    </div>
  );
}

export default EvaluationDetail;
