import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Products.css';
import { ReactComponent as SearchIcon } from '../assets/search-icon.svg';
import { ReactComponent as EyeIcon } from '../assets/eye-icon.svg';
import { ReactComponent as AddIcon } from '../assets/add-icon.svg';

function Products() {
  const [searchTerm, setSearchTerm] = useState('');
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  // Genera una fecha aleatoria entre 1 ene 2020 y hoy
  const randomPastDate = () => {
    const start = new Date(2020, 0, 1).getTime();
    const end = new Date().getTime();
    const date = new Date(start + Math.random() * (end - start));
    return date.toLocaleDateString();
  };

  useEffect(() => {
    const fetchAll = async () => {
      try {
        const token = localStorage.getItem('token');
        const headers = {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        };

        // Pide en paralelo productos, subcategorías y categorías
        const [prodRes, subRes, catRes] = await Promise.all([
          fetch('http://localhost:4000/api/productos', { headers }),
          fetch('http://localhost:4000/api/subcategorias', { headers }),
          fetch('http://localhost:4000/api/categorias', { headers }),
        ]);

        if (!prodRes.ok || !subRes.ok || !catRes.ok) {
          throw new Error('Uno de los fetch falló');
        }

        const [{ body: rawProducts }, { body: rawSubs }, { body: rawCats }] = await Promise.all([
          prodRes.json(), subRes.json(), catRes.json()
        ]);

        // Mapas para búsqueda rápida
        const subById = Object.fromEntries(rawSubs.map(s => [s.id, s]));
        const catById = Object.fromEntries(rawCats.map(c => [c.id, c]));

        // Enriquecer productos
        const enriched = rawProducts.map(p => {
          const sub = subById[p.id_subcategoria] || {};
          const categoria = catById[sub.id_categoria] || {};
          return {
            ...p,
            codigo: p.id,
            categoria: categoria.nombre || '—',
            riesgo: sub.nivel_riesgo || 'Desconocido',
            fecha: randomPastDate()
          };
        });

        setProducts(enriched);
      } catch (err) {
        console.error('Error al obtener datos para productos:', err);
      }
    };

    fetchAll();
  }, []);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleViewProduct = (id) => {
    navigate(`/productos/${id}`);
  };

  const handleAddProduct = () => {
    navigate('/productos/agregar');
  };

  const filteredProducts = products.filter(product =>
    product.nombre.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="products-container">
      <div className="search-filter-bar">
        <div className="search-bar">
          <SearchIcon className="search-icon" />
          <input
            type="text"
            placeholder="Buscar producto"
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <button className="filter-button">Agregar filtro</button>
      </div>

      <div className="products-table-container">
        <div className="products-header">
          <h2>Productos</h2>
          <button className="add-product-button" onClick={handleAddProduct}>
            <AddIcon className="add-icon" />
          </button>
        </div>

        <div className="table-wrapper">
          <table className="products-table">
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Código</th>
                <th>Categoría</th>
                <th>Riesgo</th>
                <th>Fecha</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {filteredProducts.map(product => (
                <tr key={product.id}>
                  <td>{product.nombre}</td>
                  <td>{product.codigo}</td>
                  <td>{product.categoria}</td>
                  <td className={`risk-cell ${product.riesgo.toLowerCase()}`}>
                    {product.riesgo}
                  </td>
                  <td>{product.fecha}</td>
                  <td>
                    <button
                      className="view-button"
                      onClick={() => handleViewProduct(product.id)}
                    >
                      <EyeIcon className="eye-icon" />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Products;
