import React, { useState } from 'react';
import './History.css';
import { ReactComponent as SearchIcon } from '../assets/search-icon.svg';

function History() {
  const [searchTerm, setSearchTerm] = useState('');

  // Sample data for history logs
  const historyLogs = [
    { user: 'Juan P.', date: '20/03/24', action: 'Revisar producto' },
    { user: 'Juan P.', date: '20/03/24', action: 'Revisar evaluación' },
    { user: 'Juan P.', date: '20/03/24', action: 'Iniciar sesión' },
    { user: 'Sara Z.', date: '20/03/24', action: 'Iniciar sesión' },
    { user: 'John D.', date: '20/03/24', action: 'Iniciar sesión' },
    { user: 'Mike T.', date: '20/03/24', action: 'Iniciar sesión' },
    { user: 'Davo Z.', date: '20/03/24', action: 'Iniciar sesión' },
    { user: 'Liam P.', date: '20/03/24', action: 'Iniciar sesión' },
    { user: 'Juan E.', date: '20/03/24', action: 'Iniciar sesión' },
  ];

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="history-container">
      <div className="search-filter-bar">
        <div className="search-bar">
          <SearchIcon className="search-icon" />
          <input
            type="text"
            placeholder="Buscar en historial"
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <button className="filter-button">Agregar filtro</button>
      </div>

      <div className="history-table-container">
        <h2>Historial</h2>
        <div className="table-wrapper">
          <table className="history-table">
            <thead>
              <tr>
                <th>Usuario</th>
                <th>Fecha</th>
                <th>Acción</th>
              </tr>
            </thead>
            <tbody>
              {historyLogs
                .filter(log =>
                  log.user.toLowerCase().includes(searchTerm.toLowerCase()) ||
                  log.action.toLowerCase().includes(searchTerm.toLowerCase())
                )
                .map((log, index) => (
                <tr key={index}>
                  <td>{log.user}</td>
                  <td>{log.date}</td>
                  <td>{log.action}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default History;
