import React, { useEffect, useState } from "react";
import axios from "axios";
import { useLocation, useNavigate } from "react-router-dom";

const EvaluationDetail = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const isFromDashboard = location.state?.fromDashboard;
  const evaluationId = location.state?.evaluationId || null;
  const readOnly = !isFromDashboard && !evaluationId;

  const [formData, setFormData] = useState({
    producto: "",
    riesgo: "",
    fecha: new Date().toISOString().split("T")[0],
  });

  const [productos, setProductos] = useState([]);

  useEffect(() => {
    // Obtener productos para el dropdown
    axios
      .get(`${import.meta.env.VITE_BACKEND_URL}/api/productos`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setProductos(res.data))
      .catch((err) => console.error(err));
  }, [token]);

  useEffect(() => {
    if (evaluationId && !isFromDashboard) {
      axios
        .get(`${import.meta.env.VITE_BACKEND_URL}/api/evaluaciones/${evaluationId}`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setFormData(res.data))
        .catch((err) => console.error(err));
    }
  }, [evaluationId, isFromDashboard, token]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post(`${import.meta.env.VITE_BACKEND_URL}/api/evaluaciones`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(() => navigate("/dashboard"))
      .catch((err) => console.error(err));
  };

  return (
    <div className="max-w-xl mx-auto mt-8 bg-white p-6 rounded-lg shadow">
      <h1 className="text-2xl font-bold mb-4">
        {evaluationId ? "Detalle de Evaluación" : "Nueva Evaluación"}
      </h1>
      <form onSubmit={handleSubmit}>
        {/* ID Evaluación */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">ID Evaluación</label>
          <p className="mt-1 p-2 bg-gray-100 rounded">
            {evaluationId || "Pendiente"}
          </p>
        </div>

        {/* Producto */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Producto</label>
          {readOnly ? (
            <p className="mt-1 p-2 bg-gray-100 rounded">{formData.producto}</p>
          ) : (
            <select
              name="producto"
              value={formData.producto}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
              required
            >
              <option value="">Seleccione un producto</option>
              {productos.map((producto) => (
                <option key={producto.id} value={producto.nombre}>
                  {producto.nombre}
                </option>
              ))}
            </select>
          )}
        </div>

        {/* Riesgo */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Riesgo</label>
          {readOnly ? (
            <p className="mt-1 p-2 bg-gray-100 rounded">{formData.riesgo}</p>
          ) : (
            <input
              type="text"
              name="riesgo"
              value={formData.riesgo}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
              required
            />
          )}
        </div>

        {/* Fecha */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Fecha</label>
          {readOnly ? (
            <p className="mt-1 p-2 bg-gray-100 rounded">{formData.fecha}</p>
          ) : (
            <input
              type="date"
              name="fecha"
              value={formData.fecha}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
              required
            />
          )}
        </div>

        {/* Botones */}
        <div className="flex justify-between mt-6">
          <button
            type="button"
            onClick={() => navigate(-1)}
            className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
          >
            Volver
          </button>
          {!readOnly && (
            <button
              type="submit"
              className="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
            >
              Guardar Evaluación
            </button>
          )}
        </div>
      </form>
    </div>
  );
};

export default EvaluationDetail;
