import React from 'react';
import { Link } from 'react-router-dom';
import './Hero.css';

function Hero() {
  return (
    <div className="hero">
      <div className="container">
        <div className="hero-content">
          <h1>Sistema de Evaluación de Calidad Alimentaria</h1>
          <p>Garantizamos la calidad y seguridad de los alimentos mediante evaluaciones rigurosas y sistemáticas.</p>
          <div className="hero-buttons">
            <Link to="/login" className="primary-button">Iniciar sesión</Link>
          </div>
        </div>
        <div className="hero-image">
          <img src={process.env.PUBLIC_URL + '/hero-image.jpg'} alt="Food Quality" />
        </div>
      </div>
    </div>
  );
}

export default Hero;
