import React from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import './ProductDetail.css';
import { ReactComponent as BackIcon } from '../assets/back-icon.svg';

function ProductDetail() {
  const navigate = useNavigate();
  const { id } = useParams();
  
  // In a real application, you would fetch the product details based on the ID
  // This is just placeholder data
  const productDetails = {
    code: id || '01234',
    product: 'Leche Z',
    category: 'Lácteo',
    country: 'República Dominicana',
    status: 'Activo',
    lastEvaluation: '20/03/2024',
    tariffCode: '0401.10.00',
    risk: 'Alto'
  };

  const handleGoBack = () => {
    navigate(-1);
  };

  const handleDone = () => {
    // Here you would save the product details
    console.log('Product details saved');
    navigate('/productos');
  };

  return (
    <div className="product-detail-container">
      <div className="product-detail-card">
        <div className="detail-grid">
          <div className="detail-item">
            <label>Código:</label>
            <div className="detail-value">{productDetails.code}</div>
          </div>
          
          <div className="detail-item">
            <label>Estado:</label>
            <div className="detail-value">{productDetails.status}</div>
          </div>
          
          <div className="detail-item">
            <label>Producto:</label>
            <div className="detail-value">{productDetails.product}</div>
          </div>
          
          <div className="detail-item">
            <label>Última evaluación:</label>
            <div className="detail-value">{productDetails.lastEvaluation}</div>
          </div>
          
          <div className="detail-item">
            <label>Categoría:</label>
            <div className="detail-value">{productDetails.category}</div>
          </div>
          
          <div className="detail-item">
            <label>Código arancelario:</label>
            <div className="detail-value">{productDetails.tariffCode}</div>
          </div>
          
          <div className="detail-item">
            <label>País:</label>
            <div className="detail-value">{productDetails.country}</div>
          </div>
          
          <div className="detail-item">
            <label>Riesgo:</label>
            <div className="detail-value risk-value">{productDetails.risk}</div>
          </div>
        </div>
        
        <div className="detail-actions">
          <button className="done-button" onClick={handleDone}>
            Hecho
          </button>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
