import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Users.css';
import { ReactComponent as SearchIcon } from '../assets/search-icon.svg';
import { ReactComponent as EyeIcon } from '../assets/eye-icon.svg';

function Users() {
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();
  
  // Sample data for users
  const users = [
    { name: 'Juan P.', email: 'juanp@gmail.com', role: 'Evaluador', status: 'Activo' },
    { name: 'Sara Z.', email: 'saraz@gmail.com', role: 'Evaluador', status: 'Activo' },
    { name: 'John D.', email: 'johnd@gmail.com', role: 'Evaluador', status: 'Activo' },
    { name: 'Guy U.', email: 'guyu@gmail.com', role: 'Evaluador', status: 'Activo' },
    { name: 'Didi A.', email: 'didia@gmail.com', role: 'Evaluador', status: 'Activo' },
    { name: 'Davo Z.', email: 'davoz@gmail.com', role: 'Evaluador', status: 'Activo' },
    { name: 'Alex G.', email: 'alexg@gmail.com', role: 'Evaluador', status: 'Activo' },
    { name: 'Dean A.', email: 'deana@gmail.com', role: 'Evaluador', status: 'Activo' },
  ];

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleViewUser = (email) => {
    navigate(`/usuarios/${email}`);
  };

  return (
    <div className="users-container">
      <div className="search-filter-bar">
        <div className="search-bar">
          <SearchIcon className="search-icon" />
          <input
            type="text"
            placeholder="Buscar usuarios"
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <button className="filter-button">Agregar filtro</button>
      </div>

      <div className="users-table-container">
        <h2>Usuarios</h2>
        <div className="table-wrapper">
          <table className="users-table">
            <thead>
              <tr>
                <th>Usuarios</th>
                <th>Correo electrónico</th>
                <th>Rol</th>
                <th>Estado</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {users.map((user, index) => (
                <tr key={index}>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.role}</td>
                  <td>{user.status}</td>
                  <td>
                    <button 
                      className="view-button"
                      onClick={() => handleViewUser(user.email)}
                    >
                      <EyeIcon className="eye-icon" />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Users;
