import React, { useEffect, useState } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';

const EvaluationDetail = () => {
  const { evaluationId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const isCreateMode = location.state?.isCreateMode || false;
  const fromDashboard = location.state?.from === 'dashboard';

  const [formData, setFormData] = useState({
    id: '',
    id_producto: '',
    nombre_producto: '',
    id_usuario: '',
    nombre_usuario: '',
    id_tipo_evaluacion: '',
    nombre_tipo_evaluacion: '',
    fecha_evaluacion: '',
    observaciones: ''
  });

  const token = localStorage.getItem('token');

  // Carga evaluación
  useEffect(() => {
    if (evaluationId && !isCreateMode) {
      fetch(`http://localhost:4000/api/evaluaciones/${evaluationId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(data => {
          const evaluacion = data.body;
          setFormData(prev => ({
            ...prev,
            id: evaluacion.id,
            id_producto: evaluacion.id_producto?.toString() || '',
            id_usuario: evaluacion.id_usuario?.toString() || '',
            id_tipo_evaluacion: evaluacion.id_tipo_evaluacion?.toString() || '',
            fecha_evaluacion: evaluacion.fecha_evaluacion?.substring(0, 10) || '',
            observaciones: evaluacion.observaciones || ''
          }));
        })
        .catch(err => console.error('Error al cargar evaluación:', err));
    }
  }, [evaluationId, isCreateMode, token]);

  // Carga nombre del producto
  useEffect(() => {
    if (formData.id_producto) {
      fetch(`http://localhost:4000/api/productos/${formData.id_producto}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(data => {
          setFormData(prev => ({
            ...prev,
            nombre_producto: data.body.nombre
          }));
        })
        .catch(err => console.error('Error al cargar producto:', err));
    }
  }, [formData.id_producto, token]);

  // Carga nombre del usuario
  useEffect(() => {
    if (formData.id_usuario) {
      fetch(`http://localhost:4000/api/usuarios/${formData.id_usuario}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(data => {
          setFormData(prev => ({
            ...prev,
            nombre_usuario: data.body.nombre
          }));
        })
        .catch(err => console.error('Error al cargar usuario:', err));
    }
  }, [formData.id_usuario, token]);

  // Carga tipo de evaluación
  useEffect(() => {
    if (formData.id_tipo_evaluacion) {
      fetch(`http://localhost:4000/api/tipo-criterio/${formData.id_tipo_evaluacion}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(data => {
          setFormData(prev => ({
            ...prev,
            nombre_tipo_evaluacion: data.body.nombre_tipo_evaluacion
          }));
        })
        .catch(err => console.error('Error al cargar tipo de evaluación:', err));
    }
  }, [formData.id_tipo_evaluacion, token]);

  const handleGoBack = () => {
    if (fromDashboard) {
      navigate('/dashboard');
    } else {
      navigate('/evaluaciones');
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">
        {isCreateMode ? 'Nueva Evaluación' : 'Detalle de Evaluación'}
      </h2>
      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium">ID Evaluación</label>
          <div className="border p-2 rounded bg-gray-100">{isCreateMode ? 'Pendiente' : formData.id}</div>
        </div>

        <div>
          <label className="block text-sm font-medium">Producto</label>
          <div className="border p-2 rounded bg-gray-100">{formData.nombre_producto}</div>
        </div>

        <div>
          <label className="block text-sm font-medium">Usuario</label>
          <div className="border p-2 rounded bg-gray-100">{formData.nombre_usuario}</div>
        </div>

        <div>
          <label className="block text-sm font-medium">Tipo de Evaluación</label>
          <div className="border p-2 rounded bg-gray-100">{formData.nombre_tipo_evaluacion}</div>
        </div>

        <div>
          <label className="block text-sm font-medium">Fecha</label>
          <div className="border p-2 rounded bg-gray-100">{formData.fecha_evaluacion}</div>
        </div>

        <div>
          <label className="block text-sm font-medium">Observaciones</label>
          <div className="border p-2 rounded bg-gray-100 whitespace-pre-wrap">
            {formData.observaciones}
          </div>
        </div>
      </div>

      <button
        onClick={handleGoBack}
        className="mt-6 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        Volver
      </button>
    </div>
  );
};

export default EvaluationDetail;
